  // Our built-in 'ground' shape.
  MeshBuilder.CreateGround("ground", { width: 10, height: 10 }, scene);

  //Adding sound
  const sound = new Sound("sound", "https://freesound.org/people/Connum/sounds/23874/", scene, null, {autoplay: true, loop: true});
